mapper.select:
select count(*) from surveypool
where svyno =
#{svyno,jdbcType=INTEGER}

		select count(*) from survey

		where 1=1

		
			and svtype = #{svtype,jdbcType=VARCHAR}

mapper.update:
update surveypool set title = #{title,jdbcType=VARCHAR}, type1 = #{type1,jdbcType=VARCHAR}, bogi1 = #{bogi1,jdbcType=VARCHAR}, bogi2 = #{bogi2,jdbcType=VARCHAR}, bogi3 = #{bogi3,jdbcType=VARCHAR}, bogi4 = #{bogi4,jdbcType=VARCHAR}, bogi5 = #{bogi5,jdbcType=VARCHAR},
odrno = #{odrno,jdbcType=VARCHAR}, svyno = #{svyno,jdbcType=INTEGER}
where sn = #{sn,jdbcType=INTEGER}

update survey
set svname = #{svname,jdbcType=VARCHAR},
svtype = #{svtype,jdbcType=VARCHAR},
svmemo =
#{svmemo,jdbcType=VARCHAR}
where sn = #{sn,jdbcType=INTEGER}

mapper.select:
SELECT COUNT(*) FROM (
SELECT distinct vwsvyresponse.resvyno,svname,rescnt FROM vwsvyresponse LEFT OUTER JOIN ( SELECT resvyno,COUNT(*) rescnt FROM ( SELECT distinct resvyno,uuid FROM surveyresponse ) z GROUP BY resvyno ) z ON z.resvyno = vwsvyresponse.resvyno
) zz

SELECT distinct svname,email,username,uuid
,substring(wdate,1,10) wdate FROM vwsvyresponse
WHERE uuid = #{uuid}

mapper.update:
update surveyresponse set username = #{hidusername},
email = #{hidemail}
where uuid = #{hiduuid}
