mapper.select:
SELECT utjobcode, elsname,svname,useridfk,tutorid,classnofk,dday,protitle,stuname,callername,tutorname,usyear,usban,surveyfk,spcnt FROM ( SELECT utjobcode, elsname,svname,useridfk,tutorid,classnofk,dday,protitle,stuname,callername,tutorname,usyear,usban,surveyfk,COUNT(*) spcnt FROM vwsvyresult GROUP BY utjobcode, elsname,svname,useridfk,tutorid,classnofk,dday,protitle,stuname,callername,tutorname,usyear,usban,surveyfk ) zz WHERE 1=1
and classnofk = #{cosn,jdbcType=INTEGER}
and svname like #{svname,jdbcType=VARCHAR}

		select count(*) from

		( SELECT utjobcode,
		elsname,svname,useridfk,tutorid,classnofk,dday,protitle,stuname,callername,tutorname,usyear,usban,COUNT(*)
		spcnt
		FROM vwsvyresult GROUP BY utjobcode,
		elsname,svname,useridfk,tutorid,classnofk,dday,protitle,stuname,callername,tutorname,usyear,usban
		)

		zz where 1 = 1

		
			and classnofk = #{cosn,jdbcType=INTEGER}
		
		
			and svname like #{svname,jdbcType=VARCHAR}
